
-- DROP TABLE customer;

CREATE TABLE customer (
	cust_id SERIAL PRIMARY KEY,
	company_name VARCHAR(30),
	contact_name VARCHAR(20),
	contact_position VARCHAR(20),
	address VARCHAR(50),
	city VARCHAR(20),
	region VARCHAR(20),
	phone VARCHAR(20),
	mileage INT
);

-- DROP TABLE department;

CREATE TABLE department (
	dept_id SERIAL PRIMARY KEY,
	dept_no VARCHAR(2) UNIQUE,
	dept_name VARCHAR(20)
);

-- DROP TABLE mileage_grade;

CREATE TABLE mileage_grade (
	mileage_id SERIAL PRIMARY KEY,
	grade VARCHAR(10) UNIQUE,
	min_mileage INT,
	max_mileage INT
);

-- DROP TABLE product;

CREATE TABLE product (
	product_id SERIAL PRIMARY KEY,
	product_name VARCHAR(50),
	product_unit VARCHAR(30),
	unit_price INT,
	stock INT
);

-- DROP TABLE employee;

CREATE TABLE employee (
	emp_id SERIAL PRIMARY KEY,
	emp_no VARCHAR(10) UNIQUE,
	name VARCHAR(20),
	eng_name VARCHAR(20),
	position VARCHAR(10),
	gender VARCHAR(2),
	birth_date DATE,
	hire_date DATE,
	address VARCHAR(50),
	city VARCHAR(20),
	region VARCHAR(20),
	home_phone VARCHAR(20),
	manager_no INT,
	dept_id INT,	
	FOREIGN KEY (dept_id) REFERENCES department(dept_id),
	FOREIGN KEY (manager_no) REFERENCES employee(emp_id)
);

-- DROP TABLE orders;

CREATE TABLE orders (
	order_id SERIAL PRIMARY KEY,
	order_no VARCHAR(50),
	cust_id INT,
	emp_id INT,
	order_date DATE,
	required_date DATE,
	shipped_date DATE,
	UNIQUE (order_no),	
	FOREIGN KEY (cust_id) REFERENCES customer(cust_id),
	FOREIGN KEY (emp_id) REFERENCES employee(emp_id)
);

-- DROP TABLE order_details;

CREATE TABLE order_details (
	order_detail_id SERIAL PRIMARY KEY,
	order_id INT,
	product_id INT,
	unit_price INT,
	quantity INT,
	discount FLOAT,
	UNIQUE (order_id, product_id),	
	FOREIGN KEY (order_id) REFERENCES orders(order_id),
	FOREIGN KEY (product_id) REFERENCES product(product_id)
);














-- 주문 데이터 삽입 (200개)
DO $$
DECLARE
    i INTEGER;
    random_emp INTEGER;
    random_cust INTEGER;
    random_prod INTEGER;
    random_qty INTEGER;
    random_discount FLOAT;
    order_date DATE;
    required_date DATE;
    shipped_date DATE;
    order_no TEXT;
BEGIN
    FOR i IN 1..200 LOOP
        random_emp := (SELECT emp_id FROM employee ORDER BY RANDOM() LIMIT 1);
        random_cust := (SELECT cust_id FROM customer ORDER BY RANDOM() LIMIT 1);
        random_prod := (SELECT product_id FROM product ORDER BY RANDOM() LIMIT 1);
        random_qty := (SELECT (RANDOM() * 10 + 1)::INTEGER);
        random_discount := (RANDOM() * 0.1)::FLOAT;
        order_date := NOW() - INTERVAL '1 day' * (RANDOM() * 365)::INTEGER;
        required_date := order_date + INTERVAL '1 day' * (RANDOM() * 10 + 1)::INTEGER;
        shipped_date := required_date + INTERVAL '1 day' * (RANDOM() * 5)::INTEGER;
        order_no := (select md5(random()::text) || extract(epoch from now())::INT)::TEXT;

        VALUES (random_cust, random_emp, order_date, required_date, shipped_date, order_no);

        VALUES (currval(pg_get_serial_sequence('orders','order_id')), random_prod, (SELECT unit_price FROM product WHERE product_id = random_prod), random_qty, random_discount);
    END LOOP;
END $$;




